// Custom Functions
setCookie = (key, value) => {
  document.cookie = `${key}=${value}; path=/`
}
function getCookie(name) {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) return parts.pop().split(';').shift();
}

// Jquery Operations
$(document).ready(function(){

   // Set cookie clicked language
   $(".dropdown-language .dropdown-item").on("click", function() {
       let locale = $(this).find('a').attr('data-locale');
       setCookie('index-locale', locale);
   })

  // Set cookie clicked ORDER_CHAPTER tab
   $('ul.chapter-tabs li').click(function(e)
   {
     let tabKey = $(this).find('a').attr('href').substring(1);
     setCookie('index-chapter-order', tabKey);
   });
});


